definition.script:
cfg = jplConfig('jplBuildAPKTest', 'android', '', [hipchat: '', slack: '', email: ''])

cfg.repository.url = 'https://github.com/red-panda-ci/android-testing'
cfg.repository.branch = 'master'
cfg.androidPackages = '"build-tools;27.0.1" "platforms;android-27"'

pipeline {

    agent any

    stages {
        stage ('Initialize') {
            steps  {
                jplStart(cfg)
            }
        }

        stage ('Build') {
            steps  {
                echo "jplBuildAPK Test";
                jplBuild(cfg, "./gradlew clean assembleDebug")
            }
        }
    }

    options {
        timestamps()
        ansiColor('xterm')
    }
}
