definition.script:
#!groovy

@Library('github.com/red-panda-ci/jenkins-pipeline-library') _

// Initialize cfg
cfg = jplConfig('jplPromoteBuildTest', 'backend', '', [hipchat: '', slack: '', email: ''])
cfg.BRANCH_NAME="release/v9.9.9"

// The pipeline
pipeline {

    agent none

    stages {
        stage ('Checkout') {
            agent any
            steps  {
                jplCheckoutSCM(cfg, 'file:///tmp/jenkins-pipeline-library', 'release/v9.9.9')
            }
        }
        stage ('Release confirm') {
            steps {
                timeout(time: 5, unit: 'SECONDS') {
                    jplPromoteBuild(cfg)
                }
            }
        }
    }

    options {
        timestamps()
        ansiColor('xterm')
        skipDefaultCheckout()
    }
}
